contracts/ContractSwap.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "hardhat/console.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract BNBTrade {
    address public owner;
    address public bnbTokenAddress;
    address public usdtTokenAddress;

    uint256 public exchangeRate = 200;

    event Swap(address indexed user, uint256 bnbAmount, uint256 usdtAmount);

    constructor(address _bnbTokenAddress, address _usdtTokenAddress) {
        owner = msg.sender;
        bnbTokenAddress = _bnbTokenAddress;
        usdtTokenAddress = _usdtTokenAddress;
    }

    receive() external payable {
        require(msg.sender == bnbTokenAddress, "Only BNB token is allowed");
    }

    function swap(uint256 bnbAmount) external {
        uint256 usdtAmount = bnbAmount * exchangeRate;

        require(
            IERC20(usdtTokenAddress).transferFrom(msg.sender, address(this), usdtAmount),
            "Failed to transfer USDT to contract"
        );

        (bool success, ) = msg.sender.call{value: bnbAmount}("");
        require(success, "Failed to transfer BNB to user");

        emit Swap(msg.sender, bnbAmount, usdtAmount);
    }

    function withdrawToken(address tokenAddress, uint256 amount) external {
        require(msg.sender == owner, "Only owner can call this function");
        require(
            IERC20(tokenAddress).transfer(owner, amount),
            "Failed to transfer tokens"
        );
    }

    function withdrawBNB(uint256 amount) external {
        require(msg.sender == owner, "Only owner can call this function");
        (bool success, ) = owner.call{value: amount}("");
        require(success, "Failed to transfer BNB");
    }
}

contracts/Token.sol.content:
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract Token is ERC20 {
    uint256 private _totalSupply;
    uint8 private _decimals;

    constructor(
        string memory name,
        string memory symbol,
        uint8 decimal
    ) ERC20(name, symbol) {
        _decimals = decimal;
        _totalSupply = 100_000_000 * 10 ** _decimals;
        _mint(msg.sender, _totalSupply);
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }
}

